import React, { useState } from 'react';
import { KeyboardAvoidingView, StyleSheet, Text, View, TextInput, TouchableOpacity, Keyboard, ScrollView, Alert } from 'react-native';
import Task from './components/Tasks';
import InputBox from './components/Input'; 
import Button from './components/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


export default function App() {
  return (
    <View style={styles.container}>
      <View style={styles.logo}>
      <FontAwesomeIcon style={styles.icon} icon="fa-solid fa-mug-hot" />
      </View>
      <View style={[styles.inputSettings, styles.shadowProp]} >
        <View style={styles.textPacket}>
          <Text style={styles.textInput}>Log In</Text>
        </View>
        <View>
          <InputBox text="email">
          </InputBox>
          <InputBox text="password">
          </InputBox>
        </View>
        <View style={styles.sbtnLog}>
          <Button
            title="Log in"
            color="#841584"
            onPress={() => Alert.alert('Simple Button pressed')}
          >
          </Button>
        </View>
      </View>
      <View style={styles.link}></View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    marginVertical: 20,
    // backgroundColor: 'black',
  },
  inputSettings: {
    marginTop: 150,
    backgroundColor: 'white',
    borderRadius: 8,
    paddingVertical: 35,
    paddingHorizontal: 15,
    marginVertical: 10,
    borderColor: "#606068",
    marginHorizontal: 10,
    fontFamily: 'Helvetica Neue',


  },
  shadowProp: {
    shadowOffset: { width: 1, height: 2 },
    shadowColor: '#171717',
    shadowOpacity: 0.2,
    shadowRadius: 3,
  },
  textInput: {
    textAlign: 'center',
    fontWeight: '500',
    fontSize: 30,
    color: '#a85b3c',

  },
  textPacket: {
    borderBottomColor: 'black',
    borderBottomWidth: StyleSheet.hairlineWidth,
    marginTop: -10,
    paddingBottom: 20,
    marginBottom: 30,
    borderBottomWidth: 4,
    borderBottomColor: '#a85b3c'
  },
  sbtnLog: {
    marginTop:20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    fontSize:40
  },
});
