import React, { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, View, TextInput, TouchableOpacity, Keyboard, ScrollView, Alert } from 'react-native';
import InputBox from './components/Input';
import Button from './components/Button';
import { AntDesign } from "@expo/vector-icons";
import { Fontisto } from "@expo/vector-icons";


export default function App() {
  return (
    <SafeAreaView style={styles.container}>
      <View >
        <Fontisto style={styles.logo} name="coffeescript" />
      </View>
      <View style={styles.textPacket}>
        <Text style={styles.textInput}>Log In</Text>
      </View>
      <View style={[styles.inputSettings, styles.shadowProp]} >
        <View>
          <InputBox text="email">
          </InputBox>
          <InputBox text="password">
          </InputBox>
        </View>
        <View style={styles.sbtnLog}>
          <Button
            title="Log in"
            color="#841584"
            onPress={() => Alert.alert('Simple Button pressed')}
          >
          </Button>
        </View>
      </View>
      <View style={styles.link}>
        <Text>Dont Have a Account?</Text>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    marginVertical: 20,
  },
  inputSettings: {
    marginTop: 20,
    backgroundColor: 'white',
    borderRadius: 8,
    paddingVertical: 35,
    paddingHorizontal: 15,
    marginVertical: 10,
    borderColor: "#606068",
    marginHorizontal: 10,
    fontFamily: 'Helvetica Neue',
  },
  shadowProp: {
    shadowOffset: { width: 1, height: 1 },
    shadowColor: '#171717',
    shadowOpacity: 0.2,
    shadowRadius: 3,
  },
  textInput: {
    textAlign: 'center',
    fontWeight: '500',
    fontSize: 30,
    color: '#a85b3c',
  },
  textPacket: { 
    marginTop: 30,
    borderBottomWidth: 4,
    borderBottomColor: '#a85b3c'
  },
  sbtnLog: {
    marginTop: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logo: {
    marginTop: 30,
    fontSize: 100,
    textAlign: 'center',
    justifyContent: 'center',
    color: '#a85b3c',
  },
});
